example.programlisting:
gint intval;
gchar *strval;
GObject *objval; 

g_object_get (my_object,
              "intproperty", &intval,
              "strproperty", &strval,
              "objproperty", &objval,
              NULL);

/* Do something with intval, strval, objval */

g_free (strval);
g_object_unref (objval);

refsect2.programlisting:
void        g_object_set_data               (GObject *object,
