struct GParamSpecBoolean {

  GParamSpec    parent_instance;

  gboolean      default_value;
};

struct GParamSpecChar {

  GParamSpec    parent_instance;

  gint8         minimum;
  gint8         maximum;
  gint8         default_value;
};

struct GParamSpecUChar {

  GParamSpec    parent_instance;

  guint8        minimum;
  guint8        maximum;
  guint8        default_value;
};

