  GParamSpec    parent_instance;

  GFlagsClass  *flags_class;
  guint         default_value;
};

struct GParamSpecString {

  GParamSpec    parent_instance;

  gchar        *default_value;
  gchar        *cset_first;
  gchar        *cset_nth;
